@section('script')
<script>
    function send1() {
        let token = $("#token").val();
        let name = $('#first_name').val();
        let phone = $('#phone').val();
        $.ajax({
            token: token,
            type: "get",
            url: "/feedback",
            data: {
                name: name,
                phone: phone,
            },
            contentType: "application/json; charset=utf-8",
            dataType: "json",
        });
        $('.popup__back').slideDown('200')
        $('.popup__stock').fadeOut('0');
        $('.popup__container').fadeOut('0');
        $('.popup').fadeIn('200');
        setTimeout(()=>{
            $('.popup__success').fadeIn('0');
        }, 600)
        // setTimeout(() => {
        //     $('.popup').hide()
        //     $('.popup__success').show()
        //     $("#first_name").val('');
        //     $("#phone").val('');
        // }, 1000)
        // setTimeout(() => {
        //     $('.popup').show()
        //     $('.popup__success').hide()
        //     $('.feedback').hide()
        // }, 3000)
    }
</script>
