    <script src="/js/swiper.min.js"></script>
    <script src="/js/BeerSlider.js"></script>
    <script src="/js/jquery.custom-select.js"></script>
    <script src="/js/fancybox.umd.js"></script>
    <script>
        let swiperMain = new Swiper('.main__slider', {
            spaceBetween: 50,
            slidesPerView: 1,
            speed: 800,
            parallax: true,
            loop: false,
            // effect: 'fade',
            // autoplay: {
            //     delay: 2000,
            // },
            navigation: {
                prevEl: '.main__slider-prev',
                nextEl: '.main__slider-next',
            },
            pagination: {
                el: ".main__slider-pagination",
            },
        });
        $('#calc__type').customSelect({
            placeholder: '<span style="color: darkgray;">выберите тип</span>'
        });

        $('.faq__list li').click(function() {
            $('.faq__content').slideUp('250');
            $(this).children('.faq__content').slideDown('200');
        });
        $.fn.BeerSlider = function(options) {
            options = options || {};
            return this.each(function() {
                new BeerSlider(this, options);
            });
        };
        $('.beer-slider').BeerSlider({
            start: 50
        });


        let showType = index => {
            $('.result__type span').removeClass('active')
            $('.result__box').css('display', 'none')
            $('.result__box').removeClass('result__box-active')
            $('.result__box').css('opacity', '0')
            $('.result__type span').eq(index).addClass('active')
            $('.result__box').eq(index).css('display', 'block')
            $('.result__box').eq(index).addClass('result__box-active')
            setTimeout(() => {
                $('.result__box').eq(index).css('opacity', '1')
            }, 500)
        }
        showType($('.result__type span').length - 1)

        $('.result__type span').click(function() {
            let index = $(this).index('.result__type span')
            showType(index)
            $('.beer-slider').BeerSlider({
                start: 50
            });
        })


        $('.audio__btn').click(function(event) {
            event.preventDefault();

            var audioReview = $(this).parent().find('audio')[0];
            $(".my__audio").not(audioReview).each(function() {
                this.pause();
            });
            // var audioReview = $(this).parent().find('audio')[0];
            if (audioReview.paused) {
                audioReview.play();
            } else {
                audioReview.pause();
            }
        });
    </script>
@endsection
